    int calculate_cube_index(GridCell cell, float isovalue)
    {
        int cubeIndex = 0;
        for (int i = 0; i < 8; i++)
            if (cell.value[i] < isovalue) cubeIndex |= (1 << i);
        return cubeIndex;
    }

    Vector<Point> get_intersection_coordinates(GridCell cell, float isovalue)
    {

        edgeToVertices.add( new Pair(0, 1) );
        edgeToVertices.add( new Pair(1, 2) );
        edgeToVertices.add( new Pair(2, 3) );
        edgeToVertices.add( new Pair(0, 3) );
        edgeToVertices.add( new Pair(4, 5) );
        edgeToVertices.add( new Pair(5, 6) );
        edgeToVertices.add( new Pair(6, 7) );
        edgeToVertices.add( new Pair(4, 7) );
        edgeToVertices.add( new Pair(0, 4) );
        edgeToVertices.add( new Pair(1, 5) );
        edgeToVertices.add( new Pair(2, 6) );
        edgeToVertices.add( new Pair(3, 7) );

        Vector<Point> intersections = new Vector<Point>(12);

        int cubeIndex = calculate_cube_index(cell, isovalue);
        int intersectionsKey = edgeTable[cubeIndex];

        int idx = 0;
        while (intersectionsKey != 0)
        {
            if (intersectionsKey != 0)
            {
                int v1 = edgeToVertices.get(idx).first, v2 = edgeToVertices.get(idx).second;
                Point intersectionPoint = interpolate(cell.vertex[v1], cell.value[v1],
                                                        cell.vertex[v2], cell.value[v2], isovalue);
                intersections.set(idx, intersectionPoint);
            }
            idx++;
            intersectionsKey >>= 1;
        }


        return intersections;
    }

    Point interpolate(Point v1, float val1, Point v2, float val2, float isovalue)
    {
        float mu = (isovalue - val1) / (val2 - val1);

        float interpolatedX = mu*(v2.x - v1.x) + v1.x;
        float interpolatedY = mu*(v2.y - v1.y) + v1.y;
        float interpolatedZ = mu*(v2.z - v1.z) + v1.z;
        Point interpolated = new Point(interpolatedX, interpolatedY, interpolatedZ);

        return interpolated;
    }

    Vector<Vector<Point>> get_triangles(Vector<Point> intersections, int cubeIndex)
    {
        Vector<Vector<Point>> triangles = new Vector<Vector<Point>>();
        for (int i = 0; triangleTable[cubeIndex][i] != -1; i += 3)
        {
            Vector<Point> triangle = new Vector<Point>(3);
            for (int j = 0; j < 3; j++)
                triangle.set(j, intersections.get(triangleTable[cubeIndex][i + j]));
            triangles.add(triangle);
        }

        return triangles;
    }

